using System; using System.Reflection; class Program { static void Main() { foreach (var asm in AppDomain.CurrentDomain.GetAssemblies()) { if (asm.FullName.Contains("Emoji")) { foreach (var t in asm.GetTypes()) { if (t.FullName.Contains("Smileys")) Console.WriteLine(t.FullName); } } } var emojiType = typeof(Microsoft.FluentUI.AspNetCore.Components.FluentEmoji); Console.WriteLine(emojiType.FullName); foreach (var prop in emojiType.GetProperties()) { Console.WriteLine(prop.Name + " - " + prop.PropertyType.FullName); } } }
